#!/usr/bin/env python
# -*- coding: utf-8 -*-

import sys, urllib, urllib2, json

image_data = open(sys.argv[1],'rb').read()
url ='https://aip.baidubce.com/rest/2.0/ocr/v1/general_basic?access_token=24.430535bd765bb7964fd5a6581e6f8ebf.2592000.1504834518.282335-9964286'
data = {}
data['language_type'] = "CHN_ENG"
#data['image'] = ""
data['image'] = image_data.encode('base64').replace('\n','')
decoded_data = urllib.urlencode(data)
req = urllib2.Request(url, data = decoded_data)
req.add_header("Content-Type","application/x-www-form-urlencoded")

resp = urllib2.urlopen(req)
content = resp.read()
if(content):
     print(content)
